import React from "react";
import { NavLink } from "react-router-dom";

export function Home(props){

  function removeMovie(id){
    const newList = props.movies.filter((movie)=>movie.name !== id)
    props.setMovies(newList);
  }

  function Movie(props) {
    console.log(props);
    return (
      <>
        <h2>{props.info.name}</h2>
        <img src={"./images/"+props.info.poster} alt={props.info.name} height="500px"/> 
        <p>{props.info.date}</p>
        <p>{props.info.actors[0]}, {props.info.actors[1]}, {props.info.actors[2]}</p>
        <p>{props.info.rating} out of 5 stars</p>
        <button onClick={()=> removeMovie(props.info.name)} >Remove</button>
        
      </>
    )
  }

  return (
    <div className="App">
      <Header></Header>
      <h1>Movie Reviews</h1>
      { props.movies.map( (movie) => { return <Movie key={movie.name} info={movie}></Movie>}) }
    </div>
  );
}

function Header(){
  return(
    <>
      <header>
        <nav>
          <NavLink to="/">Home</NavLink>
          <NavLink to="/AddMovie">Add Movie</NavLink>
        </nav>
      </header>
    </>
  )
}


export function AddMovie(){
    return (
        <div>
          <Header></Header>
            <h1>[AddReviews]</h1>
        </div>
    );
}
