import React from "react";
import { NavLink } from "react-router-dom";
import { useState, useRef } from "react";

export function Home({movies, setMovies}){

  

  function Movie({name, date, actors, poster, rating, onRemove = f => f}) {
    return (
      <>
        <h2>{name}</h2>
        <img src={"./images/"+poster} alt={name} height="500px"/> 
        <p>{date}</p>
        <p>{actors[0]}, {actors[1]}, {actors[2]}</p>
        <p>{rating} out of 5 stars</p>
        <button>Remove</button>
        
      </>
    )
  }

  return (
    <div className="App">
      <Header></Header>
      <h1>Movie Reviews</h1>
      { movies.map( (movie) => { return <Movie key={movie.name} info={movie}></Movie>}) }
    </div>
  );
}

function Header(){
  return(
    <>
      <header>
        <nav>
          <NavLink to="/">Home</NavLink>
          <NavLink to="/AddMovie">Add Movie</NavLink>
        </nav>
      </header>
    </>
  )
}


export function AddMovie(){
    return (
        <div>
          <Header></Header>
            <h1>[AddReviews]</h1>
        </div>
    );
}
