import React from "react";
import { useState, useEffect } from "react";

export function Home(){

  const [movies, setMovies] = useState([]);

  useEffect(() => {
    fetch('./movies.json')
      .then((response) => response.json())
      .then(setMovies)
  }, []);

  function removeMovie(id){
    const newList = movies.filter((movie)=>movie.name !== id)
    setMovies(newList);
  }

  function Movie(props) {
    console.log(props);
    return (
      <>
        <h2>{props.info.name}</h2>
        <img src={"./images/"+props.info.poster} alt={info.name} height="500px"/> 
        <p>{props.info.date}</p>
        <p>{props.info.actors[0]}, {props.info.actors[1]}, {props.info.actors[2]}</p>
        <p>{props.info.rating} out of 5 stars</p>
        <button onClick={()=> removeMovie(props.info.name)} >Remove</button>
        
      </>
    )
  }

  return (
    <div className="App">
      <h1>Movie Reviews</h1>
      { movies.map( (movie) => { return <Movie key={movie.name} info={movie}></Movie>}) }
    </div>
  );
}


export function AddReviews(){
    return (
        <div>
            <h1>[AddReviews]</h1>
        </div>
    );
}
