import React from "react";
import { NavLink } from "react-router-dom";


export function Home({movies, setMovies}){

  function removeMovie(id){
    const newList = movies.filter((movie)=>movie.name !== id)
    setMovies(newList);
  }


  function Movie( {name, date, actors, poster, rating, onRemove = f => f} ) {
    console.log(actors)
    return (
      <>
        <h2>{name}</h2>
        <img src={"./images/"+poster} alt={name} height="500px"/> 
        <p>{date}</p>
        <p>{actors}</p>
        <p>{rating} out of 5</p>
        <button onClick={() => onRemove(name)}>Remove</button>
      </>
    );
  }

  function MovieList( { movies = [], onRemoveMovie = f => f}) {
    if (!movies.length) return <div>No movies available.</div>;
    return (
        <>
            {movies.map( movie => (
                <Movie key={movie.name} {...movie} onRemove={onRemoveMovie} />
            ))}
        </>
    );
}

  return (
    <div className="App">
      <Header></Header>
      <h1>Movie Reviews</h1>
      <MovieList 
        movies={movies} 
        onRemoveMovie={ name => { const newList = movies.filter(movie => movie.name !== name);
        setMovies(newList);
      }} />
    </div>
  );
}

function Header(){
  return(
    <>
      <header>
        <nav>
          <NavLink to="/">Home</NavLink>
          <NavLink to="/AddMovie">Add Movie</NavLink>
        </nav>
      </header>
    </>
  )
}


export function AddMovie(){
    return (
        <div>
          <Header></Header>
            <h1>[AddReviews]</h1>
        </div>
    );
}
